definition.script:
def spaceName = "%SPACE%"
pipeline {
   agent any

   stages {
        stage('Cleanup') {
            steps {
                sh """
                rm -rf ./*
                """
            }
        }
        stage('Checkout') {
            steps {
                dir("UI") {
                    git 'https://QualiLabs@dev.azure.com/QualiLabs/Promotions%20Manager/_git/promotions-manager-ui'
                }
                dir("API") {
                    git 'https://QualiLabs@dev.azure.com/QualiLabs/Promotions%20Manager/_git/promotions-manager-api'
                }
                dir("Testing") {
                    git 'https://QualiLabs@dev.azure.com/QualiLabs/Promotions%20Manager/_git/Testing'
                }
            }
        }
        stage('Build') {
            steps {
                dir("UI") {
                    sh """
                    npm install
                    npm install --only=dev redux-immutable-state-invariant enzyme@3.8.0 enzyme-adapter-react-16@1.7.1 chai expect jsdom mocha react-addons-test-utils sinon
                    export REACT_APP_BUILD_NUMBER=$BUILD_NUMBER && npm run build
                    """
                }
                dir("API") {
                    sh """
                    npm install
                    npm run test:ci
                    """
                }
            }
        }
        stage('Publish artifacts') {
            steps {
                dir("UI") {
                    sh """
                    mkdir drop
                    cd build
                    tar -czvf ../drop/promotions-manager-ui.${BUILD_NUMBER}.tar.gz *
                    cd ..
                    tar -czvf promotions-manager-ui.master.tar.gz drop/promotions-manager-ui.${BUILD_NUMBER}.tar.gz
                    aws s3 cp promotions-manager-ui.master.tar.gz s3://%BUCKET_NAME%/promotions-manager/latest/
                    """
                }
                dir("API") {
                    sh """
                    rm -r .git & rm .gitignore & rm azure-pipelines.yml & rm -r test-output & rm -r test
                    mkdir drop
                    tar -czvf drop/promotions-manager-api.${BUILD_NUMBER}.tar.gz --exclude=./drop .
                    tar -czvf promotions-manager-api.master.tar.gz drop/promotions-manager-api.${BUILD_NUMBER}.tar.gz
                    aws s3 cp promotions-manager-api.master.tar.gz s3://%BUCKET_NAME%/promotions-manager/latest/
                    """
                }
                dir("Testing") {
                    sh """
                    cd data/test-db/
                    mkdir archive
                    tar -czvf archive/test-db.tar.gz --exclude=./archive .
                    aws s3 cp archive/test-db.tar.gz s3://%BUCKET_NAME%/promotions-manager/latest/
                    """
                }
            }
        }
        stage('Integration Tests') {
            steps {                
                script {

                    def newSandboxData = null

                    try {
                        //Start Sandbox
                        //TODO replace latest with current artifact from Build stage 
                        newSandboxData = colony.blueprint(
                            spaceName,                           // space name
                            'promotions-manager-all-aws',               // blueprint name
                            'pm-test-sandbox-' + env.BUILD_NUMBER,  // sandbox name
                            'PT2H',                                 // sandbox duration according to ISO 8601 duration
                            ['promotions-manager-ui': 'promotions-manager/latest/promotions-manager-ui.master.tar.gz',        // artifacts
                               'promotions-manager-api': 'promotions-manager/latest/promotions-manager-api.master.tar.gz', 
                               'mongodb': 'promotions-manager/latest/test-db.tar.gz'],    
                            ['PORT': '3000', 'API_PORT': '3001', 'RELEASE_NUMBER': 'none', 'API_BUILD_NUMBER': env.BUILD_NUMBER, 'AWS_INSTANCE_TYPE': 't3.large'],       // inputs
                            30)                                                                                                         // timeout
                            .startSandbox()

                        def endpoint = getEndpoint(newSandboxData)

                        echo 'Running integration tests against endpoint: ' + endpoint

                        // TBD - run tests
                    }
                    catch(Exception e) {
                        if (newSandboxData != null) {
                            colony.endSandbox(spaceName, newSandboxData.id)
                        }
                        print e.getClass().getName()
                        print e.message
                        throw e
                    }
                    finally {
                        if (newSandboxData != null) {
                            colony.endSandbox(spaceName, newSandboxData.id)
                        }
                    }
                }                
            }
        }
        stage('UI Tests') {
            steps {                
                echo 'Running UI tests....'

                echo 'Done'
            }
        }
        stage('Load Tests') {
            steps {
                echo 'Running load tests....'

                echo 'Done'
            }
        }
        stage('Security Tests') {
            steps {
                echo 'Running security tests....'

                echo 'Done'
            }
        }
        stage('Backwards Compatibility Tests') {
            steps {
                echo 'Running tests....'

                echo 'Done'
            }
        }
   }
}
// get the first application shortcut for the UI app
def getEndpoint(def newSandboxData) {

    def endpoint = ''

    for (app in newSandboxData.applications) {
        if (app.name == 'promotions-manager-ui') {
            endpoint = app.shortcuts[0]
            break
        }
    }

    if (endpoint == '') {
        throw new Exception('couldnt get application endpoint from sandbox details')
    }

    return endpoint
}
